#define ll long long int
const ll mod = 1e9 + 7;
ll dp[105][12][2];
ll f(string &s, int n, int p, int tight)
{

    if (n == 0)
    {
        return 1;
    }
    if(dp[n][p][tight]!=-1) return dp[n][p][tight];
    int ub = 9;
    if (tight)
    {
        ub = s[s.size() - n] - '0';
    }
    ll ans = 0;
    if (p == -1)
    {
        ans = (ans + f(s, n - 1, p, 0)) % mod;
        for (int i = 1; i <= ub; i++)
        {
            if (i == p)
            {
                continue;
            }
            if (tight and i == ub)
            {
                ans = (ans + f(s, n - 1, i, 1)) % mod;
            }
            else
            {
                ans = (ans + f(s, n - 1, i, 0)) % mod;
            }
        }
    }
    else
    {
        for (int i = 0; i <= ub; i++)
        {
            if (i == p)
            {
                continue;
            }
            if (tight and i == ub)
            {
                ans = (ans + f(s, n - 1, i, 1)) % mod;
            }
            else
            {
                ans = (ans + f(s, n - 1, i, 0)) % mod;
            }
        }
    }

    return dp[n][p][tight] = ans;
}
